import React, { useState } from 'react';
import { MapRouteEdit } from './MapRouteEdit';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';
import { Apiurl } from '../../services/apirest';

export function ModalMapRouteEdit(props) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  let routeData = undefined;
  const updateChanges = (data) => {
    routeData = data
  };
  const saveChanges = () => {
    let url = Apiurl + "/api/routes/edit/" + props.id
    axios.put(url, { points: routeData.geometry.coordinates }, { headers: { "token": localStorage.getItem("write_token") } })
      .then(res => {
        console.log(res)
      })
  };

  return (
    <>
<button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">
  Launch demo modal
</button>


<div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>

</>
);
}


