import React, { useState } from 'react';
import { MapRouteEdit } from './MapRouteEdit';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';
import { Apiurl } from '../../services/apirest';
export function ModalMapRouteEdit(props) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  let routeData = undefined;
  const updateChanges = (data) => {
    routeData = data
  };
  const saveChanges = () => {
    let url = Apiurl + "/api/routes/edit/" + props.id
    axios.put(url, { points: routeData.geometry.coordinates }, { headers: { "token": localStorage.getItem("write_token") } })
      .then(res => {
        console.log(res)
      })
  };

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Launch vertically centered modal
      </Button>

      <MyVerticallyCenteredModal
        show={modalShow}
        onHide={handleClose}
      />

      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Centered Modal</h4>
          <p>
            Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
            dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
            consectetur ac, vestibulum at eros.
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={handleClose}>Close</Button>
        </Modal.Footer>
      </Modal>
    </>

  );
}


