import React, { useEffect } from 'react';
import { RulerControl, StylesControl, ZoomControl, CompassControl } from 'mapbox-gl-controls';
import { MapboxMap } from 'react-map-gl';


export function MapView() {
  const [lng, setLng] = React.useState(-56.073358);
  const [lat, setLat] = React.useState(-34.791454);
  const [zoom, setZoom] = React.useState(11);

  const accessToken = "pk.eyJ1IjoiYm9saW9saWFndXN0aW4iLCJhIjoiY2t6eDVzcWY5NDJoNTJucW9xaHY2bXA2dCJ9.ZR3iXpsXSNMjzh9Nj-0EDQ"

  // Initialize map when component mounts
  useEffect(() => {
    const map = new MapboxMap({
      container: 'map',
      style: 'mapbox://styles/mapbox/streets-v11',
      center: [lng, lat],
      zoom,
      accessToken: accessToken
    });

    map.on('move', () => {
      setLng(map.getCenter().lng.toFixed(4));
      setLat(map.getCenter().lat.toFixed(4));
      setZoom(map.getZoom().toFixed(2));
    });


    // Add ruler control
    map.addControl(new RulerControl(), 'bottom-right');
    map.on('ruler.on', () => console.log('%cruler.on', 'color: #3D5AFE'));
    map.on('ruler.off', () => console.log('%cruler.off', 'color: #3D5AFE'));

    // with custom styles:
    map.addControl(new StylesControl({
      styles: [
        {
          label: 'Streets',
          styleName: 'Mapbox Streets',
          styleUrl: 'mapbox://styles/mapbox/outdoors-v11',
        }, {
          label: 'Satellite',
          styleName: 'Satellite',
          styleUrl: 'mapbox://styles/mapbox/satellite-v9',
        },
      ],
      onChange: (style) => console.log(style),
    }), 'top-left');

    // Add zoom control
    map.addControl(new ZoomControl(), 'bottom-right');

    // Add compass control
    map.addControl(new CompassControl(), 'bottom-right');

    // Render map
    return (
      <div id="map" style={{ height: '100vh', width: '100vw' }} />
    );
  }
  );
}
