import React, { useEffect, useState } from 'react';
import mapboxgl from '!mapbox-gl'; // eslint-disable-line import/no-webpack-loader-syntax
import { RulerControl } from 'mapbox-gl-controls';


export const MapView = () => {
  mapboxgl.accessToken = 'pk.eyJ1IjoiYm9saW9saWFndXN0aW4iLCJhIjoiY2t6eDVzcWY5NDJoNTJucW9xaHY2bXA2dCJ9.ZR3iXpsXSNMjzh9Nj-0EDQ';

  const [lng, setLng] = useState(-56.15);
  const [lat, setLat] = useState(-34.87);
  const [zoom, setZoom] = useState(11);

  // Initialize map when component mounts
  useEffect(() => {
    const map = new mapboxgl.Map({
      container: "mapContainer",
      style: 'mapbox://styles/mapbox/outdoors-v11',
      center: [lng, lat],
      zoom: [zoom],
      pitch: 45
    });

    // Add navigation control (zoom in/out)
    map.addControl(new mapboxgl.NavigationControl(), 'top-right');

    map.on('move', () => {
      setLat(map.getCenter().lat.toFixed(4));
      setLng(map.getCenter().lng.toFixed(4));
      setZoom(map.getZoom().toFixed(2));
    });

    // Add ruler control
    map.addControl(new RulerControl(), 'top-left');
    map.on('ruler.on', () => console.log('ruler.on'));
    map.on('ruler.off', () => console.log('ruler.off'));

    // Whith kilometers
    // map.addControl(new RulerControl({
    //  unit: 'kilometers',
    //  labelFormat: km => `${km.toFixed(2)} km`,
    // }), 'top-left');

    // Clean up on unmount
    return () => map.remove();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

    // Render map
    return (
      <div>
        <div className='sidebarStyle'>
          <div>
            Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
          </div>
        </div>
        <div id="mapContainer" className="map-container"/>
      </div>
    ); 
}
