import React, { useState } from 'react';
import { MapRouteEdit } from './MapRouteEdit';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';
import { Apiurl } from '../../services/apirest';

export function ModalMapRouteEdit(props) {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  let routeData = undefined;
  const updateChanges = (data) => {
    routeData = data
  };
  const saveChanges = () => {
    let url = Apiurl + "/api/routes/edit/" + props.id
    axios.put(url, { points: routeData.geometry.coordinates }, { headers: { "token": localStorage.getItem("write_token") } })
      .then(res => {
        console.log(res)
      })
  };

  return (

    <>
    <Button className="nextButton" onClick={handleShow}>
      Open Modal
    </Button>

    <Modal show={show} onHide={handleClose} size="sm">
      <Modal.Header closeButton>
        <Modal.Title>Modal heading</Modal.Title>
      </Modal.Header>
      <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleClose}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  </>
);
}


