import axios from 'axios'
import React, { Component } from 'react'
import { Table } from 'react-bootstrap'
import { Apiurl } from '../services/apirest'
import { ModalMapRouteView } from '../components/Routes/ModalMapRouteView'
import { ModalMapRouteEdit } from '../components/Routes/ModalMapRouteEdit'

export class MyRoutes extends Component {

  state = {
    data: []
  }

  username = localStorage.getItem("username");

  getRoutes = () => {
    let url = Apiurl + "/api/users/" + this.username + "/routes"
    axios.get(url)
      .then(res => {
        this.setState({ data: res.data })

      })
  }
  componentDidMount() {
    this.getRoutes();
  }

  render() {
    return (
      <div>
        <h1>My Routes</h1>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>id</th>
              <th>Line</th>
              <th>View</th>
              <th>Accion</th>
            </tr>
          </thead>
          <tbody>
            {Array.from({ length: this.state.data.length }, (v, k) => k).map(k => {
              return (
                <tr key={k}>
                  <td>{this.state.data[k]._id}</td>
                  <td>{this.state.data[k].name}</td>
                  <td><ModalMapRouteView id={this.state.data[k].id} name={this.state.data[k].name} /></td>
                  <td><ModalMapRouteEdit id={this.state.data[k].id} name={this.state.data[k].name} /></td>
                </tr>
              )
            })}
          </tbody>
        </Table>
      </div>
    )
  }
}
