import React from 'react';
import { Modal, Button } from 'react-bootstrap';

export function ModalMapBusesEdit(props) {

  localStorage.getItem("username");
  getRoutes = () => {
    let url = Apiurl + "/api/users/" + this.username + "/routes"
    axios.get(url)
      .then(res => {
        this.setState({ data: res.data });

      });
  }
  this.getRoutes();

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Map of {props.name}
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {this.state.data.map((Buses) => {
          return (
            <tr key={Buses._id}>
              <td>{Buses._id}</td>
              <td>{Buses.wifi_name}</td>
              <td> <MapBusesViewModal id={Buses._id} /></td>
              <td> <MapBusesEditModal id={Buses._id} /></td>
              <td> <button variant="danger">Delete</button></td>
            </tr>
          )
        })}
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

export function MapBusesEditModal(props) {
  const [modalShow, setModalShow] = React.useState(false);

  return (
    <>
      <Button variant="primary" onClick={() => setModalShow(true)}>
        Edit
      </Button>

      <ModalMapBusesEdit
        show={modalShow}
        id={props.id}
        name={props.name}
        onHide={() => setModalShow(false)}
      />
    </>
  );
}
