import React from "react";
import { Form, Button, Tabs, Tab, FloatingLabel, Row, Col, Alert } from "react-bootstrap";
import axios from "axios";
import { Apiurl } from "../services/apirest";

export class Login extends React.Component {

 state = {
   form:{
     "username": "",
      "password": ""
   },
    error: false,
    errorMessage: "User or password incorrect"
  }

  manejadorSubmit = (e) => {
    e.preventDefault();
  }

  manejadorChange = async (e) => {
    const { form } = this.state;
    form[e.target.name] = e.target.value;
    this.setState({ form });
  }


  manejadorButtonLogin = () => {
    let url = Apiurl + "/api/get_tokens"
    axios.post(url, this.state.form)
    .then(res => {
      if (res.status === 200) {
        console.log(res);
      }else{
        this.setState({
          error: true,
          errorMesg: res.data
        })
      }}
      )
    }


        render (){
    return (
      <React.Fragment>
        <Tabs defaultActiveKey="login" id="uncontrolled-tab-example" className="tabs-form">
          <Tab eventKey="login" title="Log in"  className="login-form">
            <div>
            <Form onSubmit={this.manejadorSubmit} > 
              <Form.Group className="mb-3" controlId="formBasicEmail">
              <FloatingLabel label="User">
                <input 
                className="form-control input-sm" 
                type="text" 
                name="username" 
                placeholder="User"
                onChange={ this.manejadorChange }
                />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
              <FloatingLabel label="Password">
                <input 
                className="form-control input-sm" 
                type="password" 
                name="password"  
                placeholder="Password"
                onChange={ this.manejadorChange }
                />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <Button 
              className="Button" 
              type="submit"
              onClick={ this.manejadorButtonLogin }>
                Submit
              </Button>
            </Form>
            {this.state.error === true &&
            <Alert variant="danger" className="DangerAlert" >
              {this.state.errorMessage}
            </Alert>
        }
          </div>
          </Tab>
          <Tab eventKey="signup" title="Sign up">
          <div className='login-form'>
            <Form>
              <Row className="mb-3">
                <Form.Group className="mb-3" as={Col} controlId="formBasicEmail">
                  <FloatingLabel label="Name">
                  <input className="form-control input-sm" type="text" name="Name" placeholder="name" />
                  </FloatingLabel>
                </Form.Group>

                <Form.Group className="mb-3" as={Col} controlId="formBasicName">
                  <FloatingLabel label="User">   
                  <input className="form-control input-sm" type="text" name="email" placeholder="User" />
                  </FloatingLabel>
                </Form.Group>
              </Row>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <FloatingLabel label="Email">
                <input className="form-control input-sm" type="email" name="email" placeholder="Email" />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
                <FloatingLabel label="Password">
                <input className="form-control input-sm" type="password" name="password" placeholder="Password" />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <Button 
              className="Button" 
              type="submit"
              //onClick={} 
              >
                Submit
              </Button>
            </Form>
          </div>
          </Tab>
        </Tabs>
      </React.Fragment>
    )
}
}
