import React, { useEffect, useState } from 'react';
import maplibregl from 'maplibre-gl';


export const MapView = () => {
  const [lng, setLng] = useState(-56.15);
  const [lat, setLat] = useState(-34.87);
  const [zoom, setZoom] = useState(11);
  const [API_KEY] = useState('1ZjMi88iKMPuhR7bkb8J');

  // Initialize map when component mounts
  useEffect(() => {
    const map = new maplibregl.Map({
      container: "mapContainer",
      style: 'https://demotiles.maplibre.org/style.json',
      center: [lng, lat],
      zoom: [zoom],
      pitch: 45
    });
    map.on('move', () => {
      setLng(map.getCenter().lng.toFixed(4));
      setLat(map.getCenter().lat.toFixed(4));
      setZoom(map.getZoom().toFixed(2));
    });


  // Add ruler control
  map.addControl(new RulerControl(), 'bottom-right');
  map.on('ruler.on', () => console.log('%cruler.on', 'color: #3D5AFE'));
  map.on('ruler.off', () => console.log('%cruler.off', 'color: #3D5AFE'));

  // with custom styles:
  map.addControl(new StylesControl({
    styles: [
      {
        label: 'Streets',
        styleName: 'Mapbox Streets',
        styleUrl: 'mapbox://styles/mapbox/outdoors-v11',
      }, {
        label: 'Satellite',
        styleName: 'Satellite',
        styleUrl: 'mapbox://styles/mapbox/satellite-v9',
      },
    ],
    onChange: (style) => console.log(style),
  }), 'top-left');

  // Add zoom control
  map.addControl(new ZoomControl(), 'bottom-right');

  // Add compass control
  map.addControl(new CompassControl(), 'bottom-right');

  // Clean up on unmount
  return () => map.remove();
}, []); // eslint-disable-line react-hooks/exhaustive-deps

  // Render map
  return (
    <div>
      <div className='sidebar'>
        Longitude: {lng} | Latitude: {lat} | Zoom: {zoom}
      </div>
      <div id="mapContainer" className="map-container"/>
    </div>
  )
}
