import {useRef, useState, useEffect} from "react";
import { Form, Button, Tabs, Tab, FloatingLabel, Row, Col, Alert } from "react-bootstrap";
import axios from "axios";
import { Apiurl } from "../services/apirest";
import { useNavigate } from "react-router-dom";

export function Login () {

const userRef = useRef();
const errRef = useRef();  
const [user, setUser] = useState("");
const [pwd, setPwd] = useState("");
const [errMsg, setErrMsg] = useState("");
const [success, setSuccess] = useState(false);

useEffect(() => {
  userRef.current.focus();
}, []);

useEffect(() => {
  setErrMsg("");
}, [user, pwd]);


  const handelSubmit = async (e) => {
    e.preventDefault();
    console.log(user, pwd);
    setUser("");
    setPwd("");
    setSuccess(true);
  };


   /* const manejadorSubmit = (e) => {
    e.preventDefault();
  }

   const manejadorChange = async (e) => {
    const { form } = this.state;
    form[e.target.name] = e.target.value;
    this.setState({ form });
  }


  const manejadorButtonLogin = () => {
    let url = Apiurl + "/api/get_tokens"
    axios.post(url, this.state.form)
    .then(res => {
      console.log(res)
      if (res.status === 200) {
      localStorage.setItem("writ_etoken", res.data.write_token);
      localStorage.setItem("read_token", res.data.read_token);

      }})
    .catch(err => {
      if (err.response) {
        this.setState({
          error: true,
          errorMessage: err.response.data
        })
      }
    })
  }
  */

    return (
      <>
      <React.Fragment>
        <Tabs defaultActiveKey="login" id="uncontrolled-tab-example" className="tabs-form">
          <Tab eventKey="login" title="Log in"  className="login-form">
            <div>
            <Form onChange={handelSubmit} >
              <Form.Group className="mb-3" controlId="formBasicEmail">
              <FloatingLabel label="User">
                <input 
                className="form-control input-sm" 
                type="text" 
                id=""
                name="username" 
                placeholder="User"
                ref={userRef}
                onChange={(e) => setUser(e.target.value)}
                value={user}
                required
                />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
              <FloatingLabel label="Password">
                <input 
                className="form-control input-sm" 
                type="password"
                id="password"
                onChange={(e) => setPwd(e.target.value)}
                value={pwd}
                required
                />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <Button 
              className="Button" 
              type="submit"
              >
                Submit
              </Button>
              <section>
                <p ref={errRef} className={errMsg ? "errmsg" : "offscreen"} aria-live="assertive"> {errMsg} </p>
              </section>
            </Form>
          </div>
          </Tab>

          <Tab eventKey="signup" title="Sign up">
          <div className='login-form'>
            <Form>
              <Row className="mb-3">
                <Form.Group className="mb-3" as={Col} controlId="formBasicEmail">
                  <FloatingLabel label="Name">
                  <input className="form-control input-sm" type="text" name="Name" placeholder="name" />
                  </FloatingLabel>
                </Form.Group>

                <Form.Group className="mb-3" as={Col} controlId="formBasicName">
                  <FloatingLabel label="User">   
                  <input className="form-control input-sm" type="text" name="email" placeholder="User" />
                  </FloatingLabel>
                </Form.Group>
              </Row>

              <Form.Group className="mb-3" controlId="formBasicEmail">
                <FloatingLabel label="Email">
                <input className="form-control input-sm" type="email" name="email" placeholder="Email" />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicPassword">
                <FloatingLabel label="Password">
                <input className="form-control input-sm" type="password" name="password" placeholder="Password" />
                </FloatingLabel>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formBasicCheckbox">
                <Form.Check type="checkbox" label="Check me out" />
              </Form.Group>
              <Button 
              className="Button" 
              type="submit"
              onClick={handelLogin}
              >
                Submit
              </Button>
            </Form>
          </div>
          </Tab>
        </Tabs>
      </React.Fragment>
      </>
    );
  }
